mapper.select:
select ifnull(sum(amount),0.00) from j_bonus_fhj

	 and u_id = #{uId} 
	and status = 1 and to_days(now()) = to_days(create_time)

select ifnull(sum(amount),0.00) from j_bonus_fhj

	 and u_id = #{uId} 
	and status = 1

select '分红奖' as name,amount,DATE_FORMAT(create_time, '%Y-%m-%d') create_time
from j_bonus_fhj

	 and u_id = #{uId} 

		and date_format(create_time, '%Y-%m-%d') >= #{beginTime}


		and date_format(create_time, '%Y-%m-%d') <= #{endTime}

	and status = 1

order by create_time desc

select ifnull(sum(amount),0.00) from j_bonus_fhj

	 and u_id = #{uId} 

		and date_format(create_time, '%Y-%m-%d') >= #{beginTime}


		and date_format(create_time, '%Y-%m-%d') <= #{endTime}

	and status = 1

select '分红奖' as name,amount,DATE_FORMAT(create_time, '%Y-%m-%d') create_time
from j_bonus_fhj

	 and u_id = #{uId} 

		and date_format(create_time, '%Y-%m') = #{month}

	and status = 1

order by create_time desc

select ifnull(sum(amount),0.00) from j_bonus_fhj

	 and u_id = #{uId} 

		and date_format(create_time, '%Y-%m') = #{month}

	and status = 1
