mapper.select:
SELECT IFNULL(SUM(parent_money),0.00) FROM j_bonus_fxj
and parent_id = #{parentId,jdbcType=INTEGER}
AND status = 1 AND to_days(now()) - to_days(created) = 1

SELECT IFNULL(SUM(parent_money),0.00) FROM j_bonus_fxj
and parent_id = #{parentId,jdbcType=INTEGER}
AND status = 1

SELECT u_name,parent_money,DATE_FORMAT(created, '%Y-%m-%d' ) created
FROM
j_bonus_fxj

	
		and parent_id = #{parentId,jdbcType=INTEGER}
	
	
		and date_format(created, '%Y-%m-%d') >= #{beginTime}
	
	
		and date_format(created, '%Y-%m-%d') <= #{endTime}
	
	AND status = 1 

ORDER BY  created DESC

SELECT IFNULL(SUM(parent_money),0.00)
FROM
j_bonus_fxj

	
		and parent_id = #{parentId,jdbcType=INTEGER}
	
	
		and date_format(created, '%Y-%m-%d') >= #{beginTime}
	
	
		and date_format(created, '%Y-%m-%d') <= #{endTime}
	
	AND status = 1 

ORDER BY  created DESC

SELECT u_name,parent_money,DATE_FORMAT(created, '%Y-%m-%d' ) created FROM j_bonus_fxj
and parent_id = #{parentId,jdbcType=INTEGER}
and date_format(created, '%Y-%m') = #{month}
AND status = 1
ORDER BY created DESC

SELECT IFNULL(SUM(parent_money),0.00) FROM j_bonus_fxj
and parent_id = #{parentId,jdbcType=INTEGER}
and date_format(created, '%Y-%m') = #{month}
AND status = 1
ORDER BY created DESC
