mapper.select:
select ifnull(sum(amount),0.00) from j_bonus_zlj

	 and u_id = #{uId} 
	 and s_id = #{sId} 
	and status = 1 and to_days(now()) - to_days(create_time) = 1

select ifnull(sum(amount),0.00) from j_bonus_zlj

	 and u_id = #{uId} 
	 and s_id = #{sId} 
	and status = 1

select s_name,amount,DATE_FORMAT(create_time, '%Y-%m-%d') create_time
from j_bonus_zlj

	 and u_id = #{uId} 
	 and s_id = #{sId} 
	and status = 1 and YEARWEEK(date_format(create_time,'%Y-%m-%d')) = YEARWEEK(now())

order by create_time desc

select s_name,amount,DATE_FORMAT(create_time, '%Y-%m-%d') create_time
from j_bonus_zlj

	 and u_id = #{uId} 
	 and s_id = #{sId} 

		and date_format(create_time, '%Y-%m-%d') >= #{beginTime}


		and date_format(create_time, '%Y-%m-%d') <= #{endTime}

	and status = 1

order by create_time desc

select ifnull(sum(amount),0.00) from j_bonus_zlj

	 and u_id = #{uId} 
	 and s_id = #{sId} 

		and date_format(create_time, '%Y-%m-%d') >= #{beginTime}


		and date_format(create_time, '%Y-%m-%d') <= #{endTime}

	and status = 1

select s_name,amount,DATE_FORMAT(create_time, '%Y-%m-%d') create_time
from j_bonus_zlj

	 and u_id = #{uId} 
	 and s_id = #{sId} 

		and date_format(create_time, '%Y-%m') = #{month}

	and status = 1

order by create_time desc

